import React,{useState} from 'react';
import styled from 'styled-components';
import Modal from '../component/newPost/modal';
import Story from '../component/activeStatus/story';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlusSquare} from '@fortawesome/free-regular-svg-icons';
import { faPlusCircle } from '@fortawesome/free-solid-svg-icons';

const IconDiv=styled.div`
  justify-self : start;
  align-self : end;
  width : 10%;
  height : 100%;
`
const MainLayout=styled.div`
  width:100vw;
  height:100vh;
  display: flex;
  flex-direction : column;
  justify-content : center;

 `

const Icond = styled.div`
  background : lightgreen;
  width : 100%;
  height : 20%;
`

function Main() {
  const [modal,setModal]=useState(false)
  function onPost(){
    setModal(true)
  }
  function onClose(){
    setModal(false)
  }

  return (
    <MainLayout>
        <IconDiv>{modal?<FontAwesomeIcon icon={faPlusCircle} size='2x'/>:<FontAwesomeIcon onClick={onPost} icon={faPlusSquare} size='2x'/>}</IconDiv>
        <Icond>
      {modal?<Modal onClose={onClose}></Modal>:null}
      <Story></Story>
      </Icond>

    </MainLayout>
  );
}

export default Main;
