import Profile from "../Profile";
import styled from "styled-components";
import React, {useState} from "react";
import {ReactComponent as CardButton} from "../../../images/cardButton.svg";
import {ReactComponent as Inbox} from "../../../images/inbox.svg";
import {ReactComponent as Comments} from "../../../images/comments.svg";
import {ReactComponent as Notifications} from "../../../images/notification.svg";
import {ReactComponent as Bookmark} from "../../../images/bookmark.svg";
import Comment from "../Comment";
import Modal from './SettingModal';
import ShareModal from "./ShareModal"
import JoinModal from "./JoinModal"

const ModalDiv=styled.div`
  width:60vw;
  height:100vh;
  background-color:rgba(0,0,0,0.6);
  display:flex;
  vertical-align: middle;
  align-items:center;
  justify-content:center;
  position:absolute;
  top:0;
  z-index:1000;
`
const PopupDiv = styled.div`
    width: 80%;
    height: 70%;
    border: 1px solid #dbdbdb;
    background-color: #ffffff;
    margin-bottom: 3.5em;
    border-radius: 12px;
    display : flex;
`

const Header = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0.75em;
`

const CardMenu = styled.div`
    display: flex;
    justify-content: space-between;
    margin: 1em 0;
`
const Interactions = styled.div`
    display: flex;
    padding-left: 1em;
`
const Icon = styled.a`
    margin-left: 0.1em;
    width: 40px;
    height: 23px;
    &:hover{
        cursor: pointer;
    }
`

function Join(props, {onClose}) {
    const{
        storyBorder,
        image,
        comments,
        likedByText,
        likedByNumber,
        hours,
    } = props;
    const [show, setShow] = useState(false);
    const [modal,setModal]=useState(false);
    const [joinmodal, setJoinModal] = useState(false)
    const [sharemodal, setShareModal] = useState(false)
    function onShare(){
        setShareModal(true)
    }
    function CloseShare(){
        setShareModal(false)
    }
    function onJoin(){
        setJoinModal(true)
    }
    function CloseJoin(){
        setJoinModal(false)
    }
    function onPost(){
        setModal(true)
    }
    function onClose(){
        setModal(false)
    }
  return (
    <ModalDiv>
        <PopupDiv>
            <Image>
                <image src = {image} alt = "card content"/>
            </Image>
            <Contents>
                <Header>
                    <Profiles><Profile iconSize = "medium" storyBorder = {storyBorder} /></Profiles>
                    <CardB onClick = {onPost}><CardButton className = "cardButton" /></CardB>
                    {modal?<Modal onClose={onClose}></Modal>:null}
                </Header>
            </Contents>
        </PopupDiv>
    </ModalDiv>
  );
}

export default Join;

const Profiles = styled.div`
    margin-bottom: 0;
`
const CardB = styled.div`
    height: 30px;
    &:hover{
        cursor: pointer;
    }
`

const Image = styled.div`
    width : 67%;
    height : 100%;
    display: block;
    border-right:solid 1px lightgray;
`
const Contents = styled.div`
    width:100%:
    height:100%;
    align-items:center;
    justify-content:center;
`
