import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes, faPhotoVideo} from '@fortawesome/free-solid-svg-icons';

const ModalDiv=styled.div`
  width:100%;
  height:100%;
  background-color:rgba(0,0,0,0.6);
  display:flex;
  align-items:center;
  justify-content:center;
  position:absolute;
  top:0;
`
const PopupDiv=styled.div`
  width: 500px;
  height: 500px;
  background-color:white;
  border-radius:12px;
  text-align:center;
`
const Close=styled.div`
  color:white;
  position:absolute;
  top:20px;
  right:20px;
`
const TitleDiv=styled.div`
  height:10%;
  border-bottom:solid 1px lightgray;
  display:flex;
  align-items:center;
  justify-content:center;
`
const Title=styled.h1`
  font-weight:700;
  font-size:1.1rem;
`
const PVicon=styled.div`
  margin:100px auto 20px;
  display:block;
`
const PVtext=styled.p`
  font-size:1.5rem;
  font-weight:300;
  margin-bottom:10px;
`
const PVbutton=styled.button`
  border:none;
  border-radius:3px;
  color:white;
  font-size:0.8em;
  font-weight:bold;
  background-color:blue;
  padding: 5px 7px;
`
function Modal({onClose}) {
  function onClick(){
    onClose()
  }
  return (
    <ModalDiv>
      <PopupDiv>
        <TitleDiv><Title>새 게시물 만들기</Title></TitleDiv>
        <div>
        <PVicon><FontAwesomeIcon icon={faPhotoVideo} size='5x'/></PVicon>
        <PVtext>사진과 동영상을 여기에 끌어다 놓으세요</PVtext>
        <PVbutton>컴퓨터에서 선택</PVbutton>
        </div>
      </PopupDiv>
      <Close onClick={onClick}><FontAwesomeIcon icon={faTimes} size='2x'/></Close>
    </ModalDiv>
  );
}

export default Modal;
