import "../../styles/menu.css";
import React, {useState} from "react";
import { ReactComponent as Home } from "../../images/home.svg";
import { ReactComponent as Inbox } from "../../images/inbox.svg";
import { ReactComponent as Create } from "../../images/create.svg";
import { ReactComponent as Searchpeople} from "../../images/searchPeople.svg";
import { ReactComponent as Pid} from "../../images/pid.svg";
import image from "../../images/profile.jpg";
import ProfileIcon from "./ProfileIcon";
import Modal from '../newPost/modal';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
function Menu (){
    const [show, setShow] = useState(false);
    const [modal,setModal]=useState(false)
    function onPost(){
    setModal(true)
    }
    function onClose(){
    setModal(false)
    }
    function onSetting(){
    window.location.assign("../setting/edit")
    }
    return (
        <div className = "menu">
            <Home className = "icon"/>
            <a href = "/"><svg aria-label="홈" class="_8-yf5 " color="#262626" fill="#262626" height="24" role="img" viewBox="0 0 24 24" width="24"><path d="M9.005 16.545a2.997 2.997 0 012.997-2.997h0A2.997 2.997 0 0115 16.545V22h7V11.543L12 2 2 11.543V22h7.005z" fill="none" stroke="currentColor" stroke-linejoin="round" stroke-width="2"></path></svg></a>
            <Inbox className = "icon"/>
            <Create className = "icon" onClick={onPost}>{modal?<FontAwesomeIcon/>:<FontAwesomeIcon onClick={onPost}/>}</Create>
            <Searchpeople className = "icon"/>
            <Pid className = "icon"/>
            <ProfileIcon iconSize = "small" image= {image} onClick={onSetting}><FontAwesomeIcon onClick={onSetting}></FontAwesomeIcon></ProfileIcon>
            {/* profileIconComponent */}
        </div>
    );
}

export default Menu;
