import "../../styles/menu.css";
import React, {useState} from "react";
import { ReactComponent as Home } from "../../images/home.svg";
import { ReactComponent as Inbox } from "../../images/inbox.svg";
import { ReactComponent as Create } from "../../images/create.svg";
import { ReactComponent as Searchpeople} from "../../images/searchPeople.svg";
import { ReactComponent as Pid} from "../../images/pid.svg";
import image from "../../images/profile.jpg";
import ProfileIcon from "./ProfileIcon";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
function Menu (){
    const [show, setShow] = useState(false);
    const [modal,setModal]=useState(false)
    function onPost(){
    setModal(true)
    }
    function onClose(){
    setModal(false)
    }
    function onSetting(){
    window.location.assign("../setting/edit")
    }
    return (
        <div className = "menu">
            <Home className = "icon"/>
            <Inbox className = "icon"/>
            <button className = "icon" img = {Create} onClick={onPost}>{modal?<FontAwesomeIcon/>:<FontAwesomeIcon onClick={onPost}/>}</button>
            <Searchpeople className = "icon"/>
            <Pid className = "icon"/>
            <ProfileIcon iconSize = "small" image= {image} onClick={onSetting}><FontAwesomeIcon onClick={onSetting}></FontAwesomeIcon></ProfileIcon>
            {/* profileIconComponent */}
        </div>
    );
}

export default Menu;
