import React from 'react';
import styled, { keyframes } from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes, faPhotoVideo} from '@fortawesome/free-solid-svg-icons';

const ModalDiv=styled.div`
  width:50%;
  height:100%;
  background-color:rgba(0,0,0,0.6);
  display:flex;
  vertical-align: middle;
  align-items:center;
  justify-content:center;
  position:absolute;
  top:0;
  z-index:1000;
`
const blowUpModal=keyframes`
  0% { transform: scale(0); } 
  100% { transform: scale(1);}
`
const PopupDiv=styled.div`
  width: 200px;
  height: 200px;
  background-color:white;
  border-radius:12px;
  text-align:center;
  animation:${blowUpModal} 0.3s cubic-bezier(0.165,0.84,0.44,1) forwards;
`
const Close=styled.div`
  color:white;
  position:absolute;
  top:20px;
  right:20px;
`
const TitleDiv=styled.div`
  height:10%;
  border-bottom:solid 1px lightgray;
  display:flex;
  align-items:center;
  justify-content:center;
`
const Title=styled.h1`
  font-weight:700;
  font-size:1.1rem;
`
function Modal({onClose}) {
  function onClick(){
    onClose()
  }
  return (
    <ModalDiv>
      <PopupDiv>
        <TitleDiv>신고</TitleDiv>
        <TitleDiv>팔로우 취소</TitleDiv>
        <TitleDiv>게시물로 이동</TitleDiv>
        <TitleDiv>공유 대상...</TitleDiv>
        <TitleDiv>링크 복사</TitleDiv>
        <TitleDiv>링크 복사</TitleDiv>
        <TitleDiv>퍼가기</TitleDiv>
        <TitleDiv onClick = {onClose}>취소</TitleDiv>
      </PopupDiv>
    </ModalDiv>
  );
}

export default Modal;
