import React from 'react';
import styled, { keyframes } from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes, faPhotoVideo} from '@fortawesome/free-solid-svg-icons';

const ModalDiv=styled.div`
  width:50vw;
  height:100vh;
  background-color:rgba(0,0,0,0.6);
  display:flex;
  vertical-align: middle;
  align-items:center;
  justify-content:center;
  position:absolute;
  top:0;
  z-index:1000;
`
const blowUpModal=keyframes`
  0% { transform: scale(0); } 
  100% { transform: scale(1);}
`
const PopupDiv=styled.div`
  width: 500px;
  height: 600px;
  background-color:white;
  border-radius:12px;
  text-align:center;
  animation:${blowUpModal} 0.3s cubic-bezier(0.165,0.84,0.44,1) forwards;
`
const Close=styled.div`
  color:white;
  position:absolute;
  top:20px;
  right:20px;
`
const TitleDiv=styled.div`
  height:10%;
  border-bottom:solid 1px lightgray;
  display:flex;
  align-items:center;
  justify-content:center;
`
const TitleSend=styled.div`
  height:18%;
  padding-left : 10px;
  border-bottom:solid 1px lightgray;
  display:flex;
  align-items:left;
  justify-content:left;
`
const Title=styled.h1`
  font-size:1rem;
`
const PVdiv=styled.div`
  padding:24px;
  width:100%:
  height:100%;
  display:flex;
  align-items:center; //정렬안들어가는 이유?
  justify-content:center;
  padding-top:100px;
`
function Modal({onClose}) {
  function onClick(){
    onClose()
  }
  return (
    <ModalDiv>
      <PopupDiv>
        <TitleDiv>
            <Title>공유</Title>
            <Close onClick={onClick}><FontAwesomeIcon icon={faTimes} size='2x'/></Close>
        </TitleDiv>
        <TitleSend><Title>받는 사람 : </Title></TitleSend>
      </PopupDiv>
      <Close onClick={onClick}><FontAwesomeIcon icon={faTimes} size='2x'/></Close>
    </ModalDiv>
  );
}

export default Modal;
