import React,{useState} from 'react';
import styled from 'styled-components';
import Modal from '../component/newPost/modal';

const Button=styled.img`
  width:24px;
  height:24px;
  float:right;
  margin-top:20px;
  margin-right: 100px;
`
const MainLayout=styled.div`
  width:100%;
  height:100vh;
  background-color:#FFEBF0;
`
function Main() {
  const [modal,setModal]=useState(false)
  function onPost(){
    // 모달을 여는 function
    setModal(true)
  }
  function onClose(){
    // 모달을 닫는 function
    setModal(false)
  }

  return (
    <MainLayout>
      <Button onClick={onPost} src='images/newPost/newPost_normal.png'/>
      {modal?<Modal onClose={onClose}></Modal>:null}
    </MainLayout>
  );
}
